//function definitions
function formatTimeStamp(UNIX_timestamp){
  var dateObject = new Date(UNIX_timestamp * 1000);
  var stamp = UNIX_timestamp;
  var weekday = dateObject.toLocaleString('de', {weekday:"short"});
  var date = dateObject.toLocaleString('de', {year:'numeric', month:'numeric', day:'numeric'});
  var shortWeekdayDate = weekday + ', ' + dateObject.toLocaleString('de', {month:'numeric', day:'numeric'});
  var time = dateObject.toLocaleString('de', { hour12: false, hour: 'numeric', minute: '2-digit'});
  return {stamp, weekday, date, shortWeekdayDate, time};      
}

function formatTemp(temp){
    if (typeof(temp)==='number'){
        var t = {temp};
        t.tempString = temp.toFixed(1) + ' C';
        return t;
    }
    else if (typeof(temp)==='object'){
        temp.maxmin = temp.max.toFixed(0) + '/' + temp.min.toFixed(0)  + 'C';
        return temp;
    }
}

function formatSpeed(speed){
  return speed.toFixed(1) + ' m/s';
}

function formatPercent(pc){
  return pc.toFixed(0) + '%';
}

function directionFromDegree(deg){
  if (deg>11.25 && deg<=33.75){return "NNE";}
  else if (deg>33.75 && deg<56.25){return "NE";}
  else if (deg>56.25 && deg<78.75){return "ENE";}
  else if (deg>78.75 && deg<101.25){return "E";}
  else if (deg>101.25 && deg<123.75){return "ESE";}
  else if (deg>123.75 && deg<146.25){return "SE";}
  else if (deg>146.25 && deg<168.75){return "SSE";}
  else if (deg>168.75 && deg<191.25){return "S";}
  else if (deg>191.25 && deg<213.75){return "SSW";}
  else if (deg>213.75 && deg<236.25){return "SW";}
  else if (deg>236.25 && deg<258.75){return "WSW";}
  else if (deg>258.75 && deg<281.25){return "W";}
  else if (deg>281.25 && deg<303.75){return "WNW";}
  else if (deg>303.75 && deg<326.25){return "NW";}
  else if (deg>326.25 && deg<348.75){return "NNW";}
  else {return "N";}
}

function formatWeatherData(weather){
    //adding new properties to subobject formatted
    weather.formatted = {};

    weather.formatted.datetime = formatTimeStamp(weather.dt);
    weather.formatted.sunrise = formatTimeStamp(weather.sunrise);
    weather.formatted.sunset = formatTimeStamp(weather.sunset);

    weather.formatted.temp = formatTemp(weather.temp);
    weather.formatted.clouds = formatPercent(weather.clouds);

    weather.formatted.wind_speed = formatSpeed(weather.wind_speed);
    weather.formatted.wind_dir = directionFromDegree(weather.wind_deg);

    weather.formatted.description = weather.weather[0].description;
    weather.formatted.iconString = 'wi wi-owm-' + weather.weather[0].icon;
    return weather.formatted;
}

